package springboot.common.request;import org.springframework.context.annotation.Configuration;import org.springframework.web.method.support.HandlerMethodArgumentResolver;import org.springframework.web.servlet.config.annotation.InterceptorRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;import springboot.common.behavior.BehaLogAspect;import javax.annotation.Resource;import java.util.List;/** * 使系统加载jar包外的文件 */@Configurationpublic class ParameterConfig implements WebMvcConfigurer {	@Resource	private BehaLogAspect behaLogAspect;	@Override	public void addArgumentResolvers(List<HandlerMethodArgumentResolver> argumentResolvers) {		argumentResolvers.add(new RequestSingleParamHandlerMethodArgumentResolver());	}	@Override	public void addInterceptors(InterceptorRegistry registry) {		registry.addInterceptor(behaLogAspect).addPathPatterns("/**");	}}