package springboot.common.utils;import com.baidu.aip.ocr.AipOcr;import org.json.JSONArray;import org.json.JSONObject;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;import java.util.HashMap;public class OCRUtils {	private static final String appId = "43647561";	private static final String secretKey = "Tu87hHn2RFMGDd3tyusgd83BM1289kZN";	private static final String apiKey = "hBMq2ttxNQxvxRrpqcYVpgwO";	public static String imageOCR(MultipartFile file) throws IOException {		AipOcr client = new AipOcr(appId, apiKey, secretKey);		byte[] imgBytes = file.getBytes();		JSONObject res = client.basicGeneral(imgBytes, new HashMap<>());		return parserResponseResult(res);	}	private static String parserResponseResult(JSONObject res) {		StringBuilder lastResult = new StringBuilder();		JSONArray jsonarray = res.getJSONArray("words_result");		for (Object o : jsonarray) {			JSONObject wordJson = (JSONObject) o;			lastResult.append(wordJson.get("words")).append("\n");		}		return lastResult.toString();	}}