package springboot.controller;import cn.dev33.satoken.annotation.SaCheckLogin;import cn.dev33.satoken.stp.StpUtil;import lombok.AllArgsConstructor;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import springboot.common.utils.GloabUtils;import springboot.common.utils.Result;@RequestMapping("auth")@RestController@AllArgsConstructorpublic class AuthController {	@RequestMapping("/isLogin")	public Result<String> isLogin() {		return Result.ok("当前会话是否登录：" + StpUtil.isLogin());	}	@RequestMapping("/isAdminLogin")	public Result<String> isAdminLogin() {		if (StpUtil.isLogin()) {			if (!GloabUtils.isAdmin()) {				StpUtil.logout();			}		}		return Result.ok("当前会话是否登录：" + StpUtil.isLogin());	}	/**	 * 退出	 */	@PostMapping(value = "logout")	@SaCheckLogin	public Result logout() {		StpUtil.logout();		return Result.ok("退出成功");	}}