INSERT INTO `content_info`(id, title, photo_url, readcount, create_time, source, comment_count, enable, sort, category_id, content, desinfo, source_url, like_count, slider, user_id, processing_time, after_process) VALUES ('1762002675825316505', '大摩：予渣打集团“增持”评级 目标价升至93.3港元', NULL, '0', '2024-02-26 15:23:41.0', '新浪新闻', '0', '0', '1', '财经', '摩根士丹利发布研究报告称，“增持”评级，日内该股股价将相对于行业上涨，%港元。报告中称，业绩后，%，年盈利预期。预测，基数较低。此外还削减成本预估，以反映新的成本节约计划，由于信贷品质有弹性，%。', ' 摩根士丹利发布研究报告称，予渣打集团（02888）“增持”评级，认为未来30日内该股股价将相对于行业上涨，目标价由89.4港元上调4.4%至93.3港元。', 'https://finance.sina.com.cn/tob/2024-02-26/doc-inakixsk4644475.shtml', '0', '0', NULL, '2024-02-26', b'1');
