INSERT INTO `content_info`(id, title, photo_url, readcount, create_time, source, comment_count, enable, sort, category_id, content, desinfo, source_url, like_count, slider, user_id, processing_time, after_process) VALUES ('1762002675825316834', '韩国2022年科技出口达152亿美元，创历史新高', NULL, '0', '2024-02-26 15:58:36.0', '新浪新闻', '0', '0', '1', '财经', '韩国科学与信息通信技术部周一公布的数据显示，在对美国出口增加的推动下，%，亿美元的历史新高。%，亿美元。由于进口增长更快，%，亿美元。从出口目的地看，由于对电子产品和游戏技术的需求增加，%，亿美元。%，亿美元，亿美元的贸易逆差。%，亿美元，%，亿美元。韩国科学与信息通信技术部表示，随着韩国科学技术的竞争力增强，倍，倍以上。', ' 韩国科学与信息通信技术部周一公布的数据显示，在对美国出口增加的推动下，韩国2022年的科技出口增长了2%，达到152亿美元的历史新高。', 'https://finance.sina.com.cn/stock/usstock/c/2024-02-26/doc-inakixsk4661359.shtml', '0', '0', NULL, '2024-02-26', b'1');
