INSERT INTO `news`(id, addtime, title, introduction, picture, content) VALUES ('91', '2023-12-21 17:56:27.0', '有梦想，就要努力去实现', '不管你想要怎样的生活，你都要去努力争取，不多尝试一些事情怎么知道自己适合什么、不适合什么呢?你说你喜欢读书，让我给你列书单，你还问我哪里有那么多时间看书;你说自己梦想的职业是广告文案，问我如何成为一个文案，应该具备哪些素质;你说你计划晨跑，但总是因为学习、工作辛苦或者身体不舒服第二天起不了床;你说你一直梦想一个人去长途旅行，但是没钱，父母觉得危险。', 'api/upload/20240226/17023666503837087_56802.jpg', '%3Cp%3E%E4%B8%8D%E7%AE%A1%E4%BD%A0%E6%83%B3%E8%A6%81%E6%80%8E%E6%A0%B7%E7%9A%84%E7%94%9F%E6%B4%BB%EF%BC%8C%E4%BD%A0%E9%83%BD%E8%A6%81%E5%8E%BB%E5%8A%AA%E5%8A%9B%E4%BA%89%E5%8F%96%EF%BC%8C%E4%B8%8D%E5%A4%9A%E5%B0%9D%E8%AF%95%E4%B8%80%E4%BA%9B%E4%BA%8B%E6%83%85%E6%80%8E%E4%B9%88%E7%9F%A5%E9%81%93%E8%87%AA%E5%B7%B1%E9%80%82%E5%90%88%E4%BB%80%E4%B9%88%E3%80%81%E4%B8%8D%E9%80%82%E5%90%88%E4%BB%80%E4%B9%88%E5%91%A2%3F%3C%2Fp%3E%3Cp%3E%E4%BD%A0%E8%AF%B4%E4%BD%A0%E5%96%9C%E6%AC%A2%E8%AF%BB%E4%B9%A6%EF%BC%8C%E8%AE%A9%E6%88%91%E7%BB%99%E4%BD%A0%E5%88%97%E4%B9%A6%E5%8D%95%EF%BC%8C%E4%BD%A0%E8%BF%98%E9%97%AE%E6%88%91%E5%93%AA%E9%87%8C%E6%9C%89%E9%82%A3%E4%B9%88%E5%A4%9A%E6%97%B6%E9%97%B4%E7%9C%8B%E4%B9%A6%3B%E4%BD%A0%E8%AF%B4%E8%87%AA%E5%B7%B1%E6%A2%A6%E6%83%B3%E7%9A%84%E8%81%8C%E4%B8%9A%E6%98%AF%E5%B9%BF%E5%91%8A%E6%96%87%E6%A1%88%EF%BC%8C%E9%97%AE%E6%88%91%E5%A6%82%E4%BD%95%E6%88%90%E4%B8%BA%E4%B8%80%E4%B8%AA%E6%96%87%E6%A1%88%EF%BC%8C%E5%BA%94%E8%AF%A5%E5%85%B7%E5%A4%87%E5%93%AA%E4%BA%9B%E7%B4%A0%E8%B4%A8%3B%E4%BD%A0%E8%AF%B4%E4%BD%A0%E8%AE%A1%E5%88%92%E6%99%A8%E8%B7%91%EF%BC%8C%E4%BD%86%E6%80%BB%E6%98%AF%E5%9B%A0%E4%B8%BA%E5%AD%A6%E4%B9%A0%E3%80%81%E5%B7%A5%E4%BD%9C%E8%BE%9B%E8%8B%A6%E6%88%96%E8%80%85%E8%BA%AB%E4%BD%93%E4%B8%8D%E8%88%92%E6%9C%8D%E7%AC%AC%E4%BA%8C%E5%A4%A9%E8%B5%B7%E4%B8%8D%E4%BA%86%E5%BA%8A%3B%E4%BD%A0%E8%AF%B4%E4%BD%A0%E4%B8%80%E7%9B%B4%E6%A2%A6%E6%83%B3%E4%B8%80%E4%B8%AA%E4%BA%BA%E5%8E%BB%E9%95%BF%E9%80%94%E6%97%85%E8%A1%8C%EF%BC%8C%E4%BD%86%E6%98%AF%E6%B2%A1%E9%92%B1%EF%BC%8C%E7%88%B6%E6%AF%8D%E8%A7%89%E5%BE%97%E5%8D%B1%E9%99%A9%E3%80%82%E5%85%B6%E5%AE%9E%EF%BC%8C%E6%88%91%E5%B7%B2%E7%BB%8F%E5%8E%8C%E5%80